function createAppend(){var e=/^\d{10}$/.exec(location.pathname.slice(1))?.[0];if(e){e=parseInt(e);let t=1<e?String(e-1).padStart(10,"0"):null,a=String(e+1).padStart(10,"0");e=`
        <div class="nav-arrows">
            ${t?`<a href="/${t}${location.search}"
                    class="nav-arrow left" title="Previous Certificate">&lt;</a>`:'<span class="nav-arrow left disabled" title="No Previous Certificate">&lt;</span>'}
            <a href="/${a}${location.search}" class="nav-arrow right" title="Next Certificate">&lt;</a>
        </div>
    `;document.body.insertAdjacentHTML("afterbegin",e),document.addEventListener("keydown",({key:e})=>{["INPUT","TEXTAREA","SELECT"].includes(document.activeElement?.tagName)||("ArrowLeft"==e&&t?location.href="/"+t+location.search:"ArrowRight"==e&&(location.href="/"+a+location.search))})}}export{createAppend};