function initNavArrows(e,t,n,o){document.body.insertAdjacentHTML("beforeend",e),document.addEventListener("keydown",({key:e})=>{["INPUT","TEXTAREA","SELECT"].includes(document.activeElement.tagName)||("ArrowLeft"==e&&t?location.href=o+"/"+t:"ArrowRight"==e&&(location.href=o+"/"+n))})}export{initNavArrows};