function createAppend({certID:e,baseURL:t}){e=parseInt(e);let a=1<e?String(e-1).padStart(10,"0"):null,r=String(e+1).padStart(10,"0");e=`
        <div class="nav-arrows">
            ${a?`<a href="${t}/${a}"
                    class="nav-arrow left" title="Previous Certificate">&lt;</a>`:'<span class="nav-arrow left disabled" title="No Previous Certificate">&lt;</span>'}
            <a href="${t}/${r}" class="nav-arrow right" title="Next Certificate">&lt;</a>
        </div>
    `;document.body.insertAdjacentHTML("beforeend",e),document.addEventListener("keydown",({key:e})=>{["INPUT","TEXTAREA","SELECT"].includes(document.activeElement?.tagName)||("ArrowLeft"==e&&a?location.href=t+"/"+a:"ArrowRight"==e&&(location.href=t+"/"+r))})}export{createAppend};