function verify({input:e,btn:t,baseURL:n}){e=e.value.trim();return e?/^\d+$/.test(e)?(t.disabled=!0,t.textContent="Retrieving...",void(location.href=n+"/"+e)):alert("Certificate number must contain only digits"):alert("Please enter a certificate number")}function initSearch({input:t,btn:n,baseURL:i}){n.onclick=()=>verify({input:t,btn:n,baseURL:i}),t.addEventListener("keydown",({key:e})=>{"Enter"==e&&verify({input:t,btn:n,baseURL:i})})}export{verify,initSearch};