import{createLogger}from"../shared/lib/log.js";function initDebugMode(){if(location.search.includes("debug")){var e=createLogger({prefix:"initDebugMode()",debugMode:!0});e.debug("Initializing app links...");let r=0;document.querySelectorAll("a[href]").forEach(e=>{var i;e.href.includes(location.origin)&&!(i=new URL(e.href)).search&&(i.search="?debug",e.href=i.toString(),r++)}),e.debug(`${r||"No"} link${1==r?"":"s"} changed!`)}}export{initDebugMode};