function initDownloadButtons(){document.querySelectorAll(".download-btn").forEach(o=>o.addEventListener("click",async e=>{e.preventDefault();var e=o.getAttribute("data-url"),t=o.getAttribute("data-filename");try{var a=URL.createObjectURL(await(await fetch(e)).blob()),n=document.createElement("a");n.style.display="none",n.href=a,n.download=t,document.body.append(n),n.click(),URL.revokeObjectURL(a),document.body.removeChild(n)}catch(t){open(e,"_blank")}}))}export{initDownloadButtons};