function verify(e,t,i){e=e.value.trim();return e?/^\d+$/.test(e)?(t.disabled=!0,t.textContent="Retrieving...",void(location.href=i+"/"+e)):alert("Certificate number must contain only digits"):alert("Please enter a certificate number")}function initSearch(t,i,n){i.onclick=()=>verify(t,i,n),t.addEventListener("keydown",({key:e})=>{"Enter"==e&&verify(t,i,n)})}export{verify,initSearch};